    private void botonMostrarReporteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonMostrarReporteActionPerformed
        String mensaje = "";

        botones.add(botonMasc);
        botones.add(botonFem);
        
        mensaje += "Nombres: " + campoNombres.getText() + "\n";
        mensaje += "Apellidos: " + campoApellidos.getText() + "\n";
        if (botonMasc.isSelected()) {
            mensaje += "Genero: Masculino\n";
        } else if (botonFem.isSelected()) {
            mensaje += "Genero: Femenino\n";
        } else {
            mensaje += " \n";
        }
        mensaje += "Estado civil: " + comboEstadoCivil.getSelectedItem().toString();

        if (campoNombres.getText().trim().isEmpty()||campoApellidos.getText().trim().isEmpty()||botones.isSelected(null)) {
            JOptionPane.showMessageDialog(null, "Completa el formulario!");
        } else {
            cajaTexto.setText(mensaje);
        }
    }//GEN-LAST:event_botonMostrarReporteActionPerformed

